want to proceed with this action?",            type: "warning",            confirmButtonText: "Confirm",            showCancelButton: true,            cancelButtonText: "Close",            customClass: {                confirmButton: 'btn btn-primary',                cancelButton: 'btn btn-danger',            },        }).then((result) => {            if (!result.value) return;            $.ajax({                url: action + '/' + id,                type: "GET",                dataType: "json",                cache: false,                success: function(res) {                    console.log('Action executed successfully.');                    window.location.reload();                },                error: function(xhr, error, thrown) {                    console.error('Error executing action:', error);                }            });        });    });</script>