        <th>Quotation</th>                        <th>Date</th>                        <th>Customer</th>                        <th><?php echo $this->lang->line('model') ?></th>                        <th style="width: 150px"><?php echo $this->lang->line('VIN') ?></th>                        <th style="width: 150px"><?php echo $this->lang->line('OE') ?></th>                        <th><?php echo $this->lang->line('description') ?></th>                        <th><?php echo $this->lang->line('user') ?></th>                        <th data-no-search="0"><?php echo $this->lang->line('total') ?></th>                        <th data-no-search="0"><?php echo $this->lang->line('pickup') ?></th>                        <th data-no-search="0"><?php echo $this->lang->line('checked') ?></th>                        <th style="width: 150px" class="text-center"                            data-no-search="0"><?php echo $this->lang->line('actions') ?></th>                        <th class="text-center"                            data-no-search="0"><?php echo $this->lang->line('transfered') ?></th>                    </tr>                    </thead>                    <tbody class="list">                    <?php foreach ($records as $record) { ?>                        <tr>                            <td><?php echo $record['auto_no'] ?></td>                            <td><?php echo $record['qu_no'] ?></td>                            <td><?php echo $record['trans_date'] ?></td>                            <td><?php echo $record['account1'] ?></td>                            <td><?php echo $record['model'] ?></td>                            <td><?php echo $record['VIN'] ?></td>                            <td><?php echo $record['OE'] ?></td>                            <td><?php echo $record['description'] ?></td>                            <td><?php echo $record['user_name'] ?></td>                            <td><?php echo $record['total'] ?></td>        