ations/view/";                                } elseif ($record['trans_type'] == "SA") {                                    $link = "sales/view/";                                } else {                                    $link = "orders/view/";                                }                                ?>                                <tr>                                    <td><?php echo $count++ ?></td>                                    <td><?php echo $this->lang->line($record['trans_type']) ?></td>                                    <td><?php echo $record['auto_no'] ?></td>                                    <td><?php echo $record['account_name'] . " - " . $record['account_number'] ?></td>                                    <td><?php echo $record['trans_date'] ?></td>                                    <td><?php echo $record['pfand'] . ' ' . $record['currency_code'] ?></td>                                    <td><a href="<?php echo $link . $record['id'] ?>"><i style="color:#282828;"                                                                                         class="fas fa-info"></i></a>                                    </td>                                </tr>                            <?php } ?>                            </tbody>                        </table>                    </div>                <?php } ?>            </div>        </div>    </div></div>